import React, { useState } from 'react';
import { diffWordsWithSpace, diffLines } from 'diff'; // Functions for word and line diff

function JsonComparator() {
    const [inputOne, setInputOne] = useState('');
    const [inputTwo, setInputTwo] = useState('');
    const [differences, setDifferences] = useState('');

    const handleFileChange = (event, setter) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = (e) => {
                setter(e.target.result);
            };
            reader.readAsText(file);
        }
    };

    const compareByLine = () => {
        const diff = diffLines(inputOne, inputTwo);
        formatDiff(diff);
    };

    const compareByWord = () => {
        const diff = diffWordsWithSpace(inputOne, inputTwo);
        formatDiff(diff);
    };

    const formatDiff = (diff) => {
        const formattedDiff = diff.map((part, index) => (
            <span key={index} style={{ color: part.added ? 'green' : part.removed ? 'red' : 'black' }}>
                {part.value}
            </span>
        ));
        setDifferences(formattedDiff);
    };

    return (
        <div>
            <h1>Compare JSON/Code</h1>
            <div>
                <input type="file" onChange={(e) => handleFileChange(e, setInputOne)} />
                <textarea
                    value={inputOne}
                    onChange={e => setInputOne(e.target.value)}
                    rows="10"
                    cols="50"
                    placeholder="Enter or upload JSON/code..."
                />
            </div>
            <div>
                <input type="file" onChange={(e) => handleFileChange(e, setInputTwo)} />
                <textarea
                    value={inputTwo}
                    onChange={e => setInputTwo(e.target.value)}
                    rows="10"
                    cols="50"
                    placeholder="Enter or upload JSON/code..."
                />
            </div>
            <div>
                <button onClick={compareByLine}>Compare by Line</button>
                <button onClick={compareByWord}>Compare by Word</button>
            </div>
            <div>
                <h2>Differences:</h2>
                <div style={{ whiteSpace: 'pre-wrap' }}>{differences}</div>
            </div>
        </div>
    );
}

export default JsonComparator;
