import React, { useState } from 'react';
import { diffLines } from 'diff';  // Importing the function for line-level diffs

function JsonComparator() {
    const [inputOne, setInputOne] = useState('');
    const [inputTwo, setInputTwo] = useState('');
    const [differences, setDifferences] = useState('');

    const handleInputOneChange = (event) => {
        setInputOne(event.target.value);
    };

    const handleInputTwoChange = (event) => {
        setInputTwo(event.target.value);
    };

    const compareInputs = () => {
        const diff = diffLines(inputOne, inputTwo);
        if (diff.length === 1 && !diff[0].added && !diff[0].removed) {
            setDifferences('No differences found.');
        } else {
            let lineNumber = 1;
            const formattedDiff = diff.map((part, index) => {
                const lines = part.value.split('\n');
                // Construct line number display
                const lineNumbers = lines.slice(0, lines.length - 1).map((_, lineIndex) => {
                    return `${lineNumber++}`;
                }).join('\n');

                const style = {
                    color: part.added ? 'green' : part.removed ? 'red' : 'black',
                    textDecoration: part.added ? 'underline' : part.removed ? 'line-through' : 'none'
                };

                return (
                    <div key={index}>
                        <pre style={{ display: 'inline', marginRight: '10px' }}>{lineNumbers}</pre>
                        <span style={style}>{part.value}</span>
                    </div>
                );
            });
            setDifferences(<div style={{ whiteSpace: 'pre-wrap', lineHeight: '1.6' }}>{formattedDiff}</div>);
        }
    };

    return (
        <div>
            <h1>JSON/Code Comparator</h1>
            <textarea
                value={inputOne}
                onChange={handleInputOneChange}
                rows="10"
                cols="50"
                placeholder="Enter JSON or code..."
            />
            <textarea
                value={inputTwo}
                onChange={handleInputTwoChange}
                rows="10"
                cols="50"
                placeholder="Enter JSON or code..."
            />
            <div>
                <button onClick={compareInputs}>Compare</button>
            </div>
            <div>
                <h2>Differences:</h2>
                {differences || <p>No differences detected or input is empty.</p>}
            </div>
        </div>
    );
}

export default JsonComparator;
