import React, { useState } from 'react';
import { diffLines, diffJson } from 'diff';  // Importing diff functions

function JsonComparator() {
    const [inputOne, setInputOne] = useState('');
    const [inputTwo, setInputTwo] = useState('');
    const [differences, setDifferences] = useState('');

    const handleInputOneChange = (event) => {
        setInputOne(event.target.value);
    };

    const handleInputTwoChange = (event) => {
        setInputTwo(event.target.value);
    };

    const compareJson = () => {
        try {
            const jsonOne = JSON.parse(inputOne);
            const jsonTwo = JSON.parse(inputTwo);
            const diff = diffJson(jsonOne, jsonTwo);
            const formattedDiff = diff.map(part => {
                const color = part.added ? 'green' : part.removed ? 'red' : 'grey';
                return <span style={{ color: color }}>{part.value}</span>;
            });
            setDifferences(formattedDiff);
        } catch (error) {
            setDifferences(<p style={{ color: 'red' }}>Error parsing JSON: {error.message}</p>);
        }
    };

    const compareText = () => {
        const diff = diffLines(inputOne, inputTwo);
        const formattedDiff = diff.map(part => {
            const color = part.added ? 'green' : part.removed ? 'red' : 'grey';
            return <span style={{ color: color }}>{part.value}</span>;
        });
        setDifferences(formattedDiff);
    };

    return (
        <div>
            <h1>JSON/Code Comparator</h1>
            <textarea
                value={inputOne}
                onChange={handleInputOneChange}
                rows="10"
                cols="50"
                placeholder="Enter JSON or code..."
            />
            <textarea
                value={inputTwo}
                onChange={handleInputTwoChange}
                rows="10"
                cols="50"
                placeholder="Enter JSON or code..."
            />
            <div>
                <button onClick={compareJson}>Compare JSON</button>
                <button onClick={compareText}>Compare Text</button>
            </div>
            <div>
                <h2>Differences:</h2>
                {differences}
            </div>
        </div>
    );
}

export default JsonComparator;
