import React, { useState, useRef } from 'react';
import './JsonTool.css';

function JsonTool() {
    const [jsonInput, setJsonInput] = useState('');
    const [error, setError] = useState('');
    const [lineNumbers, setLineNumbers] = useState('1');
    const [historyVisible, setHistoryVisible] = useState(false);
    const [history, setHistory] = useState(() => JSON.parse(sessionStorage.getItem('jsonHistory')) || []);
    const textareaRef = useRef(null);
    const lineNumbersRef = useRef(null);

    const handleInputChange = (event) => {
        setJsonInput(event.target.value);
        setError('');
        updateLineNumbers(event.target.value);
    };

    const updateLineNumbers = (text) => {
        const lineCount = text.split('\n').length;
        const lineNumbersArray = Array.from({ length: lineCount }, (_, i) => i + 1).join('\n');
        setLineNumbers(lineNumbersArray);
    };

    const handleScroll = () => {
        if (lineNumbersRef.current) {
            lineNumbersRef.current.scrollTop = textareaRef.current.scrollTop;
        }
    };

    return (
        <div className='json-wrapper' style={{ display: 'flex' }}>
            <div className='line-numbers' ref={lineNumbersRef} style={{
                width: '50px',
                height: '162px',
                overflow: 'hidden',
                textAlign: 'right',
                marginRight: '10px',
                background: '#eee',
                padding: '8px',
                fontFamily: '"Courier New", Courier, monospace',
                fontSize: '16px',
                lineHeight: '1.5'
            }}>
                {lineNumbers}
            </div>
            <textarea
                ref={textareaRef}
                className='json-text-area'
                value={jsonInput}
                onChange={handleInputChange}
                onScroll={handleScroll}
                rows="10"
                cols="50"
                placeholder="Enter JSON here..."
                style={{ resize: 'none' }}
            />
            {/* Buttons and error messages */}
        </div>
    );
}

export default JsonTool;
