import React, { useState, useRef } from 'react';

function JsonTool() {
    const [jsonInput, setJsonInput] = useState('');
    const [error, setError] = useState('');
    const [lineNumbers, setLineNumbers] = useState('1'); // Start with line number 1
    const [historyVisible, setHistoryVisible] = useState(false);
    const [history, setHistory] = useState(
        () => JSON.parse(sessionStorage.getItem('jsonHistory')) || []
    );
    const textareaRef = useRef(null);
    const lineNumbersRef = useRef(null);

    const handleInputChange = (event) => {
        setJsonInput(event.target.value);
        setError(''); // Clear previous errors when user edits text
        updateLineNumbers(event.target.value);
    };

    const validateJson = () => {
        try {
            const parsedJson = JSON.parse(jsonInput);
            const formattedJson = JSON.stringify(parsedJson, null, 2);
            setJsonInput(formattedJson); // Update textarea with formatted JSON
            updateLineNumbers(formattedJson); // Update line numbers based on formatted JSON
            updateHistory(formattedJson); // Save to history
            setError('');
            alert('JSON is valid');
        } catch (err) {
            setError(`Invalid JSON! Error: ${err.message}`);
        }
    };

    const compressJson = () => {
        try {
            const parsedJson = JSON.parse(jsonInput);
            const compressedJson = JSON.stringify(parsedJson);
            setJsonInput(compressedJson); // Compress JSON by removing spaces
            updateLineNumbers(compressedJson); // Update line numbers for compressed JSON
            updateHistory(compressedJson); // Save to history
            setError('');
            alert('JSON has been compressed.');
        } catch (err) {
            setError(`Cannot compress JSON! Error: ${err.message}`);
        }
    };

    const clearJson = () => {
        setJsonInput('');
        setError('');
        setLineNumbers('1'); // Reset line numbers
    };

    const updateLineNumbers = (text) => {
        const lines = text.split('\n');
        const newLineNumbers = lines.map((_, index) => index + 1).join('\n');
        setLineNumbers(newLineNumbers);
        if (lineNumbersRef.current) {
            lineNumbersRef.current.scrollTop = textareaRef.current.scrollTop;
        }
    };

    const handleScroll = () => {
        if (lineNumbersRef.current) {
            lineNumbersRef.current.scrollTop = textareaRef.current.scrollTop;
        }
    };

    const updateHistory = (json) => {
        const newEntry = { json, timestamp: new Date().toLocaleString() };
        const newHistory = [newEntry, ...history].slice(0, 50); // Keep only the latest 50 entries
        setHistory(newHistory);
        sessionStorage.setItem('jsonHistory', JSON.stringify(newHistory));
    };

    const toggleHistory = () => {
        setHistoryVisible(!historyVisible);
    };

    return (
        <div>
            <h1>JSON Tool</h1>
            <textarea
                ref={textareaRef}
                value={jsonInput}
                onChange={handleInputChange}
                onScroll={handleScroll}
                rows="10"
                cols="50"
                placeholder="Enter JSON here..."
                style={{ resize: 'none' }}
            />
            <div>
                <button onClick={validateJson}>Validate and Expand JSON</button>
                <button onClick={compressJson}>Compress JSON</button>
                <button onClick={clearJson}>Clear</button>
                <button onClick={toggleHistory}>{historyVisible ? 'Hide History' : 'Show History'}</button>
            </div>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {historyVisible && (
                <ul>
                    {history.map((entry, index) => (
                        <li key={index}>{entry.timestamp}: {entry.json}</li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default JsonTool;
