import React, { useState } from 'react';

function JsonTool() {
    const [jsonInput, setJsonInput] = useState('');
    const [error, setError] = useState('');

    const handleInputChange = (event) => {
        setJsonInput(event.target.value);
        setError(''); // Clear previous errors when user edits text
    };

    const validateJson = () => {
        try {
            const parsedJson = JSON.parse(jsonInput);
            // Format JSON with 2 spaces indentation
            const formattedJson = JSON.stringify(parsedJson, null, 2);
            setJsonInput(formattedJson); // Update textarea with formatted JSON
            setError('');
            alert('JSON is valid and has been expanded.');
        } catch (err) {
            setError(`Invalid JSON! Error: ${err.message}`);
        }
    };

    const compressJson = () => {
        try {
            const parsedJson = JSON.parse(jsonInput);
            setJsonInput(JSON.stringify(parsedJson)); // Compress JSON by removing spaces
            setError('');
            alert('JSON has been compressed.');
        } catch (err) {
            setError(`Cannot compress JSON! Error: ${err.message}`);
        }
    };

    const clearJson = () => {
        setJsonInput('');
        setError('');
    };

    return (
        <div>
            <h1>JSON Tool</h1>
            <textarea
                value={jsonInput}
                onChange={handleInputChange}
                rows="100"
                cols="170"
                placeholder="Enter JSON here..."
            />
            <div>
                <button onClick={validateJson}>Validate and Expand JSON</button>
                <button onClick={compressJson}>Compress JSON</button>
                <button onClick={clearJson}>Clear</button>
            </div>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
}

export default JsonTool;
